<script>
    import { Meteor } from 'meteor/meteor';
    import { formatSpecial, formatChat } from '../../../tools';
    import { s_user } from '../../../api/stores';

    import { Commands } from '../../../api/collections/c_commands';
    let subCommands = Meteor.subscribe('commands');
    let commands = [];

    import { Responses } from '../../../api/collections/c_responses';
    let subResponses = Meteor.subscribe('responses');
    let responses = [];

    let user = undefined;
    s_user.subscribe(newValue => user = newValue);

    let bLeftPanel = true;
    let imei;
    let session;
    let bPanelRight = false;
    let singleCommand;
    let sessionChatTime;
    const fTogglePanelLeft = ()=> bLeftPanel = !bLeftPanel
    
    const fSendMessage = () =>{
        if(bPanelRight){
            Meteor.call('commands.origin',imei, singleCommand);
        }
    }

    const fGetSession = () => {
        if(Number(imei)){
            Meteor.call('users.messages.set.imei',imei);
            Meteor.call('sessions.getSingle',imei, (error,result)=> {
                if(!error && result){
                    session = result;
                    bPanelRight = true;
                    sessionChatTime = new Date();

                }else{
                    bPanelRight = false;
                    session = {
                        createdTime : "Dispositivo sin conexion anterior"
                    }
                }
            })
        }
    }
    if(user && user.profile && user.profile.messages){
        imei = user.profile.messages.imei
    }
    $m:{
        if(session && sessionChatTime && subCommands.ready()){
            commands = Commands.find({imei, createdTime: {$gte: sessionChatTime}}).fetch();
            
        }
        if(session && sessionChatTime && subResponses.ready()){
            responses = Responses.find({imei, receivedTime: {$gte: sessionChatTime}}).fetch();
  
        }
    }
</script>
<!-- CONTAINER -->
<div class="dark:bg-dark-700 bg-dark-50 h-[calc(100%_-_40px)] flex">
    <!-- LEFT -->   
    {#if bLeftPanel}
        <div class="grid grid-rows-[50px,1fr] shadow-lg basis-[300px] text-xs">

            <!-- BAR -->
            <div class="flex justify-end px-4 items-center"> 

            </div>
            <!-- BAR_END -->
            <!-- OPTIONS -->
            <div class="flex flex-col p-10 gap-6">
                <div>
                    <h1 class="text-dark-700 dark:text-dark-100 font-bold ">BUSCAR DISPOSITIVO</h1>
                </div>


                <div class="grid grid-rows-[auto,auto] gap-1">
                    <label for="imei" class="text-dark-700 dark:text-dark-300 "
                        >IMEI:</label>
                        <input
                        class="h-8 ring-1 ring-dark-300 rounded px-3 dark:text-dark-800 focus:outline-none focus:ring-alfa-500"
                        type="text"
                        name="imei"

                        bind:value={imei}
                    />
                </div>
                <div>
                    <button disabled={!imei} on:click={fGetSession} class="bg-alfa-600 shadow uppercase  text-white  rounded w-full h-8" > consultar</button>
                </div>
                {#if session}
                    <div class="grid grid-rows-[auto,auto] gap-1">
                        <label for="imei" class="text-dark-700 dark:text-dark-300 ">Ultima conexion:</label>
                        <div class="flex items-center justify-center h-8 ring-1 rounded px-3 dark:text-dark-800 ring-alfa-500">
                            {formatSpecial(session.createdTime) ? formatSpecial(session.createdTime) : session.createdTime}
                        </div>
                    </div>
                {/if}
                <!-- <div>
                    <button disabled={!imei} class="bg-beta-600 shadow uppercase  text-white  rounded w-full h-8" > iniciar comunicacion</button>
                </div> -->
           </div>
            <!-- OPTIONS_END -->

        </div>
    {/if}
    <!-- LEFT_END -->

    <!-- RIGHT -->
    {#if bPanelRight}
    <!-- flex flex-col justify-between flex-1 -->
    <div class="p-5 h-full text-xs grid grid-rows-[56px,1fr,56px] w-full">  
        <div class="flex items-center gap-5 h-14 bg-alfa-600 px-5">
            <div>
                <button class="h-9 w-9 rounded-full bg-white shadow-lg grid place-content-center" on:click={fTogglePanelLeft}>
                    {#if bLeftPanel}
                        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512" class="h-5 w-5 fill-alfa-700 dark:fill-dark-100"><path d="M32 32C14.3 32 0 46.3 0 64v96c0 17.7 14.3 32 32 32s32-14.3 32-32V96h64c17.7 0 32-14.3 32-32s-14.3-32-32-32H32zM64 352c0-17.7-14.3-32-32-32s-32 14.3-32 32v96c0 17.7 14.3 32 32 32h96c17.7 0 32-14.3 32-32s-14.3-32-32-32H64V352zM320 32c-17.7 0-32 14.3-32 32s14.3 32 32 32h64v64c0 17.7 14.3 32 32 32s32-14.3 32-32V64c0-17.7-14.3-32-32-32H320zM448 352c0-17.7-14.3-32-32-32s-32 14.3-32 32v64H320c-17.7 0-32 14.3-32 32s14.3 32 32 32h96c17.7 0 32-14.3 32-32V352z"/></svg>
                    {/if}
                    {#if !bLeftPanel}
                        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512" class="h-5 w-5 fill-alfa-700 dark:fill-dark-100"><path d="M456 224H312c-13.3 0-24-10.7-24-24V56c0-9.7 5.8-18.5 14.8-22.2s19.3-1.7 26.2 5.2l40 40L442.3 5.7C446 2 450.9 0 456 0s10 2 13.7 5.7l36.7 36.7C510 46 512 50.9 512 56s-2 10-5.7 13.7L433 143l40 40c6.9 6.9 8.9 17.2 5.2 26.2s-12.5 14.8-22.2 14.8zm0 64c9.7 0 18.5 5.8 22.2 14.8s1.7 19.3-5.2 26.2l-40 40 73.4 73.4c3.6 3.6 5.7 8.5 5.7 13.7s-2 10-5.7 13.7l-36.7 36.7C466 510 461.1 512 456 512s-10-2-13.7-5.7L369 433l-40 40c-6.9 6.9-17.2 8.9-26.2 5.2s-14.8-12.5-14.8-22.2V312c0-13.3 10.7-24 24-24H456zm-256 0c13.3 0 24 10.7 24 24V456c0 9.7-5.8 18.5-14.8 22.2s-19.3 1.7-26.2-5.2l-40-40L69.7 506.3C66 510 61.1 512 56 512s-10-2-13.7-5.7L5.7 469.7C2 466 0 461.1 0 456s2-10 5.7-13.7L79 369 39 329c-6.9-6.9-8.9-17.2-5.2-26.2s12.5-14.8 22.2-14.8H200zM56 224c-9.7 0-18.5-5.8-22.2-14.8s-1.7-19.3 5.2-26.2l40-40L5.7 69.7C2 66 0 61.1 0 56s2-10 5.7-13.7L42.3 5.7C46 2 50.9 0 56 0s10 2 13.7 5.7L143 79l40-40c6.9-6.9 17.2-8.9 26.2-5.2s14.8 12.5 14.8 22.2V200c0 13.3-10.7 24-24 24H56z"/></svg>
                    {/if}
                </button>
            </div>

            <form class="flex items-center gap-5 h-[50px] resize" on:submit|preventDefault={fSendMessage}>

                <input required bind:value={singleCommand} type="text" class="h-9 w-[280px] shadow-lg ring-1 ring-dark-300 rounded-2xl px-3 dark:text-dark-800 focus:outline-none focus:ring-alfa-500">
                <button type="submit" disabled={!singleCommand} class="h-9 w-9 rounded-full bg-white shadow-lg grid place-content-center">
                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512" class="h-5 w-5 fill-alfa-700 dark:fill-dark-100"><path d="M498.1 5.6c10.1 7 15.4 19.1 13.5 31.2l-64 416c-1.5 9.7-7.4 18.2-16 23s-18.9 5.4-28 1.6L277.3 424.9l-40.1 74.5c-5.2 9.7-16.3 14.6-27 11.9S192 499 192 488V392c0-5.3 1.8-10.5 5.1-14.7L362.4 164.7c2.5-7.1-6.5-14.3-13-8.4L170.4 318.2l-32 28.9 0 0c-9.2 8.3-22.3 10.6-33.8 5.8l-85-35.4C8.4 312.8 .8 302.2 .1 290s5.5-23.7 16.1-29.8l448-256c10.7-6.1 23.9-5.5 34 1.4z"/></svg>
                </button>
            </form>
        </div>

        <!-- CHAT -->
        <div class="bg-special flex  overflow-auto p-3">
            <!-- RESPONSES -->
            <div class="flex-1 flex flex-col ">
                {#if subResponses.ready()}
                    {#each responses as response }
                        <div class="rounded-sm flex">
                            <div class="bg-beta-200 h-10 px-5 mb-3  overflow-auto flex items-center min-w-[400px] rounded-xl">
                                <div class="whitespace-nowrap">
                                    <p class="text-dark-700">{response.response}</p>
                                    <p class="text-dark-600" style="font-size: 10px;">{formatChat(response.receivedTime)}</p>
                                </div>
                            </div>
                        </div>
                    {/each}
                {/if}
            </div>
            <!-- RESPONSES_END -->
            <!-- COMMANDS -->
            <div class="flex-1 flex flex-col ">
                {#if subCommands.ready()}
                    {#each commands as command}
                        <div class="rounded-sm flex justify-end ">
                            <div class="bg-alfa-200 h-10 px-5 mb-3 overflow-auto flex justify-end items-center min-w-[200px] rounded-xl">
                                <div class="whitespace-nowrap">
                                    <p class="text-dark-700">{command.commandText}</p>
                                    <p class="text-dark-600" style="font-size: 10px;">{formatChat(command.createdTime)}</p>
                                </div>
                            </div>
                        </div>
                    {/each}                    
                {/if}
            </div>
            <!-- COMMANDS_END -->
        </div>
        <!-- CHAT_END -->
        <div class="bg-alfa-600 h-14"></div>

    </div>
    {/if}
    <!-- RIGHT_END -->
</div>
<!-- CONTAINER_END -->
<style>
